codeExamples.code:
let xValues = [[1, 2],
               [3, 4]] as [[Float]]
let yValues = [[5, 6],
               [7, 8]] as [[Float]]

let context = try BNNSGraph.makeContext {
    builder in

    let x = builder.constant(values: xValues)
    let y = builder.constant(values: yValues)

    let z = x.matmul(other: y)

    return [z] // On return, `z` equals `[19.0, 22.0, 43.0, 50.0]`.
}
