let ctx = try BNNSGraph.makeContext {

    builder in

    let z = builder.argument(dataType: Bool.self, shape: [8])

    let y = builder.argument(dataType: Float.self, shape: [8])

    let masked = z.select(Float.infinity, y)

    return [ masked ]
}

