let coefficients: [Float] = [10, 20, 30]
let variables: [Float] = [7, 5]

let result = vDSP.evaluatePolynomial(usingCoefficients: coefficients,
                                     withVariables: variables)

// Prints "[660.0, 380.0]".
//    result[0] = (10 * 7²) + (20 * 7¹) + (30 * 7⁰) = 660
//    result[1] = (10 * 5²) + (20 * 5¹) + (30 * 5⁰) = 380
print(result)
