codeExamples.code:
If Flag & vDSP_HALF_WINDOW:
    Length = (N+1)/2;
Else
    Length = N;

If Flag & vDSP_HANN_NORM:
    W = .8165;
Else
    W = .5;

for (n = 0; n < Length; ++n)
    C[n] = W * (1 - cos(2*pi*n/N));

let n = vDSP_Length(1024)
var c = [Float](repeating: 0,
                count: Int(n))

vDSP_hann_window(&c,
                 n,
                 Int32(vDSP_HANN_DENORM))
