let x: [Double] = [3, 6, 5, 9]
let y: [Double] = [4, 8, 12, 12]

let hypotenuses = [Double](
    unsafeUninitializedCapacity: x.count) {
        buffer, initializedCount in

        vDSP.hypot(x, y,
                   result: &buffer)

        initializedCount = x.count
    }

// Prints "[5.0, 10.0, 13.0, 15.0]".
print(hypotenuses)
