var initialValue: Double = 0
let increment: Double = 1

let ramp = vDSP.ramp(withInitialValue: &initialValue,
                     multiplyingBy: [10, 100, 10, 100, 10, 100, 10, 100],
                     increment: increment)

// Prints "[0.0, 100.0, 20.0, 300.0, 40.0, 500.0, 60.0, 700.0]".
print(ramp)

// Prints "8".
print(initialValue)
