let stride = vDSP_Stride(1)

let a: [Float] = [-1.5, 2.25, 3.6,
                  0.2, -0.1, -4.3]
let n = vDSP_Length(a.count)

var c: Float = .nan

vDSP_svesq(a,
           stride,
           &c,
           n)

// Prints "sum of squares 38.8125"
print(String(format: "sum of squares %.4f", c))
