let stride = vDSP_Stride(1)

let a: [Int32] = [10, 20, 30, 40, 50]
let b: [Int32] = [ 1,  2,  3,  4,  5]

let n = vDSP_Length(a.count)

var c = [Int32](repeating: 0,
                count: a.count)

vDSP_vdivi(b, stride,
           a, stride,
           &c, stride,
           n)

// Prints "[10, 10, 10, 10, 10]"
print(c)
