let stride = vDSP_Stride(1)

let a: [Int32] = [0, 0, 1, 1]
let b: [Int32] = [0, 1, 0, 1]

let n = vDSP_Length(a.count)

var c = [Int32](repeating: 0,
                count: a.count)

vDSP_veqvi(b, stride,
           a, stride,
           &c, stride,
           n)

// Prints "[-1, -2, -2, -1]"
print(c)
