// The `coefficientMatrix` array contains the diagonal values of
// the symmetric Toeplitz coefficient matrix `a` in `a · f = c`.
let coefficientMatrix: [Float] = [15, 60, 3, 9]

// The `constants` array contains the RHS `c` in `a · f = c`.
let constants: [Float] = [360, 564, 816, 510]

var performanceIndex = [Float](repeating: 0,
                               count: constants.count)
var error = Int32(0)

// On return, `result` contains the solution `f` in `a · f = c`.
let result = [Float](unsafeUninitializedCapacity: constants.count) {
    buffer, count in

    vDSP_wiener(vDSP_Length(constants.count),
                coefficientMatrix,
                constants,
                buffer.baseAddress!,
                &performanceIndex, 0,
                &error)

    count = constants.count
}

guard error == 0 else {
    fatalError("Solve failed at pass \(error).")
}

// Prints `[1.9999962, 3.9999976, 6.000002, 7.9999995] ≈ [2, 4, 6, 8]`
print(result)
