codeExamples.code:
let src = vImage.PixelBuffer<vImage.InterleavedFx4>(
    pixelValues: [0.25, 0.5, 0.75, 1.0],
    size: vImage.Size(width: 1, height: 1))

let dest = vImage.PixelBuffer<vImage.Interleaved8x4>(
    size: src.size)

let factor = Float(255)
src.applyPolynomial(coefficientSegments: [ [1 * factor, 0, 0],
                                           [0, 1 * factor, 0],
                                           [0, 0, 1 * factor] ],
                    boundaries: [0, 1/3, 2/3, 1] as [Float],
                    destination: dest)

// Prints:
//  255     ≅ factor * 0.25⁰

//  127     ≅ factor * 0.5¹

//  143     ≅ factor * 0.75²
//  255     ≅ factor * 1.0²
print(dest.array)
