codeExamples.code:
let src = vImage.PixelBuffer<vImage.Planar8>(
    pixelValues: [ 10, 11, 12, 13, 14,
                   20, 21, 22, 23, 24,
                   30, 31, 32, 33, 34,
                   40, 41, 42, 43, 44,
                   50, 51, 52, 53, 54 ],
    size: vImage.Size(width: 5,
                      height: 5))

let dest = vImage.PixelBuffer<vImage.Planar8>(
    size: vImage.Size(width: 3,
                      height: 3))

src.crop(at: CGPoint(x: 1, y: 1),
         destination: dest)

// Prints:
//  [ 21, 22, 23,
//    31, 32, 33,
//    41, 42, 43 ]
print(dest.array)
