let buffer = vImage.PixelBuffer<vImage.Planar8>(
     pixelValues: [10, 20, 30, 40,
                   50, 60, 70, 80],
     size: vImage.Size(width: 4,
                       height: 2))

 buffer.multiply(by: 2,
                 divisor: 1,
                 preBias: 0, postBias: 5,
                 destination: buffer)

 // Prints
 // [  25,  45,  65,  85,
 //   105, 125, 145, 165 ]
 print(buffer.array)
