do {
    let patternResults = try await NSPasteboard.general.detectedPatterns(for: [\.calendarEvents])
    if patternResults.contains(\.calendarEvents) {
        print("Calendar event(s) detected.")
    } else {
        print("Didn't detect any calendar events.")
    }
} catch {
    print("Error: \(error).")
}
