do {
    let valueResults = try await NSPasteboard.general.detectedValues(for: [\.links])
    let links = valueResults.links
    guard !links.isEmpty else {
        print ("No links found in item.")
        return
    }
    for link in links {
        print("Link retrieved: \(link.url).")
    }
} catch {
    print("Error: \(error).")
}
