func dot (inputImage: CIImage) -> CIImage {
    let dotScreen = CIFilter.dotScreen()
    dotScreen.inputImage = inputImage
    dotScreen.angle = 0
    dotScreen.center = CGPoint(x: 2016, y: 1512)
    dotScreen.width = 35
    dotScreen.sharpness = 0.7
    return dotScreen.outputImage!
}
