let x = MLTensor(shape: [2, 3], scalars: [
    10, 30, 20,
    60, 40, 50
], scalarType: Float.self)
let i = MLTensor(shape: [2, 1], scalars: [
    1,
    0
], scalarType: Int32.self)
let updates = MLTensor(shape: [2, 1], scalars: [
    99,
    99
], scalarType: Float.self)
let y = x.replacing(atIndices: i, with: updates, alongAxis: 1)
// [[10, 99, 20],
//  [99, 40, 50]]
