let stringColumn = MLUntypedColumn(["1", "2", "3", "4", "5"])
print(stringColumn)
/* Prints...
 ValueType: String
 Values:        [1, 2, 3, 4, 5]
 */

let intsColumn = MLUntypedColumn(ints: stringColumn)
print(intsColumn)
/* Prints...
 ValueType: Int
 Values:        [1, 2, 3, 4, 5]
 */
