let stride = vDSP_Stride(1)

let a: [Float] = [10, 20, 30, 40, 50]
let b: [Float] = [ 1,  2,  3,  4,  5]

let n = vDSP_Length(a.count)

var c = [Float](repeating: 0,
                count: a.count)

vDSP_vdiv(b, stride,
          a, stride,
          &c, stride,
          n)

// Prints "[10, 10, 10, 10, 10]".
print(c)
