codeExamples.code:
let stride = vDSP_Stride(1)

let a: [Float] = [-1.5, 2.25, 3.6,
                  0.2, -0.1, -4.3]
let n = vDSP_Length(a.count)

var c: Float = .nan

vDSP_svs(a,
         stride,
         &c,
         n)

// Prints "sum of signed squares -2.6875".
print(String(format: "sum of signed squares %.4f", c))
