let stride = vDSP_Stride(1)

let a: [Float] = [1, 2, 3, 4, 5]
let b: [Float] = [10, 20, 30, 40, 50]

let n = vDSP_Length(a.count)

var c = [Float](repeating: 0,
                count: a.count)

vDSP_vsub(b, stride,
          a, stride,
          &c, stride,
          n)

// Prints "[-9.0, -18.0, -27.0, -36.0, -45.0]".
print(c)
