codeExamples.code:
let stride = vDSP_Stride(1)

let a: [Float] = [1, 2, 4, 5]
var b: Float = 2

let n = vDSP_Length(a.count)

var c = [Float](repeating: 0,
                count: a.count)

vDSP_vsmul(a, stride,
           &b,
           &c, stride,
           n)

// Prints "[2.0, 4.0, 8.0, 10.0]".
print(c)
