*C = +INFINITY;
for (n = 0; n < N; ++n)
    if (*C > A[n*I])
        *C = A[n*I];

let stride = vDSP_Stride(1)

let a: [Float] = [-1.5, 2.25, 3.6,
                  0.2, -0.1, -4.3]
let n = vDSP_Length(a.count)

var c: Float = .nan

vDSP_minv(a,
          stride,
          &c,
          n)

print("min", c) // Prints "min -4.3"
