codeExamples.code:
let stride = vDSP_Stride(1)

var a: Float = 2
let b: [Float] = [1, 2, 4, 5]


let n = vDSP_Length(b.count)

var c = [Float](repeating: 0,
                count: b.count)

vDSP_svdiv(&a,
           b, stride,
           &c, stride,
           n)

// Prints "[2, 1, 0.5, 0.4]"
print(c)
