@main
class BackgroundUploadExtension: PHBackgroundResourceUploadExtension {
    required init() {}

    func process() -> PHBackgroundResourceUploadProcessingResult {
        // Process upload jobs.
        return .completed
    }

    func notifyTermination() {
        // Prepare for termination.
    }
