class ViewDelegate: NSObject, SKViewDelegate {
    var lastRenderTime: TimeInterval = 0

    let fps: TimeInterval = 3

    public func view(_ view: SKView, shouldRenderAtTime time: TimeInterval) -> Bool {

        if time - lastRenderTime >= 1 / fps {
            lastRenderTime = time
            return true
        }
        else {
            return false
        }

    }
}
