let x = ([0, 1, 2, 3, 4, 5] as [Float])[1...4]
let y = ([4, 5, 6, 7, 8, 9] as [Float])[1...4]

let context = try BNNSGraph.makeContext {
    builder in

    let x = x.graphBuilderTensor(builder)

    let z = x.matmul(transpose: true,
                     other: y)

    return [z] // On return, `z[0]` equals `70`.
}
