var values: [Int] = [0, 1, 2, 3]

// Don't use 'reserveCapacity(_:)' like this
func addTenQuadratic() {
    let newCount = values.count + 10
    values.reserveCapacity(newCount)
    for n in values.count..<newCount {
        values.append(n)
    }
}

func addTen() {
    let newCount = values.count + 10
    for n in values.count..<newCount {
        values.append(n)
    }
}
