struct StepperView: View {
    @State private var value = 0.0
    let step = 5.0
    let range = 1.0...50.0

    var body: some View {
        Stepper(value: $value,
                in: range,
                step: step,
                format: .number) {
            Text("Current: \(value) in \(range.description) " +
                 "stepping by \(step)")
        }
            .padding(10)
    }
}
