@State private var isScrollDisabled = false

var body: some View {
    ScrollView {
        VStack {
            Toggle("Disable", isOn: $isScrollDisabled)
            MyContent()
        }
    }
    .scrollDisabled(isScrollDisabled)
}

ScrollView {
    ForEach(rows) { row in
        ScrollView(.horizontal) {
            RowContent(row)
        }
    }
}
.scrollDisabled(true)
