struct Complication: View {
    @Environment(\.widgetFamily) var widgetFamily

    var body: some View {
        switch widgetFamily {
        case .accessoryCorner:
            Text("Hi")
                .widgetLabel {
                    Gauge(value: 0.8)
                }
        case .accessoryCircular:
            VStack {
                Image(systemName: "emoji.globe")
                Text("Hi")
            }
            .widgetLabel("World!")
        case .accessoryInline:
            Text("\(Image(systemName: "emoji.globe.face")) World!")
    }
}
