var body: some View {
    VStack {
        TextField("User Name", text: $userName)
            .textContentType(.username)
            .multilineTextAlignment(.center)
        SecureField("Password", text: $password)
            .textContentType(.password)
            .multilineTextAlignment(.center)
        Button("Done") {
        }.disabled(userName.isEmpty || password.isEmpty)
    }
}
